INSERT INTO public.lessons (level, lesson_number, title, description, vocabulary, grammar_points, estimated_hours) VALUES
('N3', 1, 'Ôn tập N4 & Giới thiệu N3', 'Ôn tập kiến thức N4 và làm quen với cấu trúc N3', '[{"word": "可能性", "reading": "かのうせい", "meaning": "khả năng", "example": "成功の可能性がある"}, {"word": "経験", "reading": "けいけん", "meaning": "kinh nghiệm", "example": "経験を積む"}, {"word": "準備", "reading": "じゅんび", "meaning": "chuẩn bị", "example": "準備をする"}]', '[{"pattern": "〜によって", "meaning": "tùy thuộc vào, do", "example": "人によって意見が違う"}, {"pattern": "〜に対して", "meaning": "đối với", "example": "先生に対して敬語を使う"}]', 3),
('N3', 2, 'Động từ tiềm năng nâng cao', 'Học cách sử dụng động từ tiềm năng trong các tình huống phức tạp', '[{"word": "見える", "reading": "みえる", "meaning": "có thể nhìn thấy", "example": "富士山が見える"}, {"word": "聞こえる", "reading": "きこえる", "meaning": "có thể nghe thấy", "example": "音楽が聞こえる"}, {"word": "分かる", "reading": "わかる", "meaning": "hiểu", "example": "日本語が分かる"}]', '[{"pattern": "〜られる (可能形)", "meaning": "có thể làm", "example": "泳げる、食べられる"}, {"pattern": "〜ことができる", "meaning": "có thể làm", "example": "日本語を話すことができる"}]', 2),
('N3', 3, 'Thể bị động cơ bản', 'Học cách tạo và sử dụng câu bị động trong tiếng Nhật', '[{"word": "褒める", "reading": "ほめる", "meaning": "khen ngợi", "example": "先生に褒められた"}, {"word": "叱る", "reading": "しかる", "meaning": "mắng", "example": "母に叱られた"}, {"word": "壊す", "reading": "こわす", "meaning": "phá hủy", "example": "子供に壊された"}]', '[{"pattern": "〜られる (受身形)", "meaning": "bị/được", "example": "先生に褒められた"}, {"pattern": "〜に〜される", "meaning": "bị ai đó làm gì", "example": "友達に笑われた"}]', 3),
('N3', 4, 'Thể sai khiến', 'Học cách sử dụng thể sai khiến để yêu cầu người khác làm việc', '[{"word": "手伝わせる", "reading": "てつだわせる", "meaning": "bắt giúp đỡ", "example": "弟に手伝わせる"}, {"word": "待たせる", "reading": "またせる", "meaning": "bắt đợi", "example": "友達を待たせた"}, {"word": "行かせる", "reading": "いかせる", "meaning": "cho đi", "example": "子供を学校に行かせる"}]', '[{"pattern": "〜させる", "meaning": "bắt/cho làm", "example": "息子を勉強させる"}, {"pattern": "〜させてください", "meaning": "xin cho tôi làm", "example": "行かせてください"}]', 3),
('N3', 5, 'Thể sai khiến bị động', 'Kết hợp thể sai khiến và bị động', '[{"word": "残業", "reading": "ざんぎょう", "meaning": "làm thêm giờ", "example": "残業させられた"}, {"word": "掃除", "reading": "そうじ", "meaning": "dọn dẹp", "example": "掃除させられる"}, {"word": "発表", "reading": "はっぴょう", "meaning": "phát biểu", "example": "発表させられた"}]', '[{"pattern": "〜させられる", "meaning": "bị bắt làm", "example": "残業させられた"}, {"pattern": "〜せられる", "meaning": "bị bắt làm (ngắn gọn)", "example": "行かせられる→行かされる"}]', 3),
('N3', 6, 'Kính ngữ cơ bản', 'Học cách sử dụng kính ngữ trong giao tiếp', '[{"word": "いらっしゃる", "reading": "いらっしゃる", "meaning": "đến/đi/ở (kính ngữ)", "example": "先生がいらっしゃる"}, {"word": "召し上がる", "reading": "めしあがる", "meaning": "ăn/uống (kính ngữ)", "example": "コーヒーを召し上がる"}, {"word": "おっしゃる", "reading": "おっしゃる", "meaning": "nói (kính ngữ)", "example": "先生がおっしゃった"}]', '[{"pattern": "お〜になる", "meaning": "kính ngữ", "example": "お読みになる"}, {"pattern": "〜れる/られる", "meaning": "kính ngữ", "example": "先生が来られる"}]', 3),
('N3', 7, 'Khiêm nhường ngữ', 'Học cách sử dụng khiêm nhường ngữ khi nói về bản thân', '[{"word": "参る", "reading": "まいる", "meaning": "đi/đến (khiêm nhường ngữ)", "example": "明日参ります"}, {"word": "申す", "reading": "もうす", "meaning": "nói (khiêm nhường ngữ)", "example": "田中と申します"}, {"word": "いただく", "reading": "いただく", "meaning": "nhận/ăn (khiêm nhường ngữ)", "example": "お茶をいただく"}]', '[{"pattern": "お〜する", "meaning": "khiêm nhường ngữ", "example": "お送りする"}, {"pattern": "〜いたす", "meaning": "làm (khiêm nhường ngữ)", "example": "お待ちいたします"}]', 3),
('N3', 8, 'Biểu đạt ý kiến và suy nghĩ', 'Cách diễn đạt ý kiến và suy nghĩ một cách tự nhiên', '[{"word": "意見", "reading": "いけん", "meaning": "ý kiến", "example": "意見を述べる"}, {"word": "考え", "reading": "かんがえ", "meaning": "suy nghĩ", "example": "考えを伝える"}, {"word": "印象", "reading": "いんしょう", "meaning": "ấn tượng", "example": "良い印象を与える"}]', '[{"pattern": "〜と思う", "meaning": "tôi nghĩ rằng", "example": "面白いと思う"}, {"pattern": "〜んじゃないか", "meaning": "có lẽ, chắc là", "example": "雨が降るんじゃないか"}]', 2),
('N3', 9, 'Diễn đạt lý do nâng cao', 'Các cách diễn đạt lý do phức tạp hơn', '[{"word": "理由", "reading": "りゆう", "meaning": "lý do", "example": "理由を説明する"}, {"word": "原因", "reading": "げんいん", "meaning": "nguyên nhân", "example": "事故の原因"}, {"word": "結果", "reading": "けっか", "meaning": "kết quả", "example": "良い結果"}]', '[{"pattern": "〜ため(に)", "meaning": "vì, để", "example": "試験のために勉強する"}, {"pattern": "〜せいで", "meaning": "vì, do (tiêu cực)", "example": "雨のせいで遅れた"}]', 2),
('N3', 10, 'Điều kiện và giả định', 'Học cách diễn đạt điều kiện và giả định', '[{"word": "条件", "reading": "じょうけん", "meaning": "điều kiện", "example": "条件を満たす"}, {"word": "場合", "reading": "ばあい", "meaning": "trường hợp", "example": "雨の場合"}, {"word": "状況", "reading": "じょうきょう", "meaning": "tình huống", "example": "状況を見る"}]', '[{"pattern": "〜ば", "meaning": "nếu", "example": "行けば分かる"}, {"pattern": "〜なら", "meaning": "nếu", "example": "暇なら来て"}, {"pattern": "〜としたら", "meaning": "nếu giả sử", "example": "行くとしたら明日"}]', 3),
('N3', 11, 'Biểu đạt mục đích', 'Cách nói về mục đích và ý định', '[{"word": "目的", "reading": "もくてき", "meaning": "mục đích", "example": "目的を達成する"}, {"word": "予定", "reading": "よてい", "meaning": "dự định", "example": "予定を立てる"}, {"word": "計画", "reading": "けいかく", "meaning": "kế hoạch", "example": "計画を作る"}]', '[{"pattern": "〜ように", "meaning": "để mà", "example": "忘れないように書く"}, {"pattern": "〜ために", "meaning": "để", "example": "合格するために勉強する"}]', 2),
('N3', 12, 'Thời gian và sự thay đổi', 'Diễn đạt về thời gian và sự thay đổi', '[{"word": "途中", "reading": "とちゅう", "meaning": "giữa chừng", "example": "途中で止める"}, {"word": "最中", "reading": "さいちゅう", "meaning": "đang giữa lúc", "example": "会議の最中"}, {"word": "変化", "reading": "へんか", "meaning": "thay đổi", "example": "変化がある"}]', '[{"pattern": "〜うちに", "meaning": "trong lúc", "example": "若いうちに"}, {"pattern": "〜につれて", "meaning": "theo, cùng với", "example": "年を取るにつれて"}]', 2),
('N3', 13, 'So sánh nâng cao', 'Các cách so sánh phức tạp hơn', '[{"word": "比較", "reading": "ひかく", "meaning": "so sánh", "example": "比較する"}, {"word": "違い", "reading": "ちがい", "meaning": "sự khác biệt", "example": "違いがある"}, {"word": "同じ", "reading": "おなじ", "meaning": "giống nhau", "example": "同じ意見"}]', '[{"pattern": "〜ほど", "meaning": "càng...càng", "example": "勉強するほど分かる"}, {"pattern": "〜より〜のほうが", "meaning": "...hơn", "example": "春より夏のほうが好き"}]', 2),
('N3', 14, 'Mức độ và phạm vi', 'Diễn đạt về mức độ và phạm vi', '[{"word": "程度", "reading": "ていど", "meaning": "mức độ", "example": "ある程度"}, {"word": "範囲", "reading": "はんい", "meaning": "phạm vi", "example": "範囲を決める"}, {"word": "限度", "reading": "げんど", "meaning": "giới hạn", "example": "限度を超える"}]', '[{"pattern": "〜くらい/ぐらい", "meaning": "khoảng, đến mức", "example": "泣きたいくらい"}, {"pattern": "〜ほど", "meaning": "đến mức", "example": "死ぬほど"}]', 2),
('N3', 15, 'Liệt kê và ví dụ', 'Cách liệt kê và đưa ra ví dụ', '[{"word": "例", "reading": "れい", "meaning": "ví dụ", "example": "例を挙げる"}, {"word": "種類", "reading": "しゅるい", "meaning": "loại", "example": "いろいろな種類"}, {"word": "様々", "reading": "さまざま", "meaning": "đa dạng", "example": "様々な人"}]', '[{"pattern": "〜とか〜とか", "meaning": "...chẳng hạn như", "example": "りんごとかバナナとか"}, {"pattern": "〜など", "meaning": "...v.v", "example": "本など"}]', 2),
('N3', 16, 'Truyền đạt thông tin', 'Cách truyền đạt và báo cáo thông tin', '[{"word": "情報", "reading": "じょうほう", "meaning": "thông tin", "example": "情報を集める"}, {"word": "知らせ", "reading": "しらせ", "meaning": "tin tức", "example": "知らせを受ける"}, {"word": "連絡", "reading": "れんらく", "meaning": "liên lạc", "example": "連絡する"}]', '[{"pattern": "〜そうだ (伝聞)", "meaning": "nghe nói rằng", "example": "雨が降るそうだ"}, {"pattern": "〜によると", "meaning": "theo như", "example": "ニュースによると"}]', 2),
('N3', 17, 'Sự việc xảy ra', 'Diễn đạt về các sự việc xảy ra', '[{"word": "発生", "reading": "はっせい", "meaning": "xảy ra", "example": "事故が発生する"}, {"word": "起こる", "reading": "おこる", "meaning": "xảy ra", "example": "問題が起こる"}, {"word": "生じる", "reading": "しょうじる", "meaning": "phát sinh", "example": "困難が生じる"}]', '[{"pattern": "〜てしまう", "meaning": "đã...mất rồi (tiếc nuối)", "example": "忘れてしまった"}, {"pattern": "〜ところだ", "meaning": "đang/vừa mới", "example": "今出たところだ"}]', 2),
('N3', 18, 'Phỏng đoán và dự đoán', 'Cách phỏng đoán và dự đoán', '[{"word": "予想", "reading": "よそう", "meaning": "dự đoán", "example": "予想する"}, {"word": "推測", "reading": "すいそく", "meaning": "suy đoán", "example": "推測する"}, {"word": "見込み", "reading": "みこみ", "meaning": "triển vọng", "example": "見込みがある"}]', '[{"pattern": "〜そうだ (様態)", "meaning": "trông có vẻ", "example": "雨が降りそうだ"}, {"pattern": "〜らしい", "meaning": "có vẻ", "example": "学生らしい"}]', 2),
('N3', 19, 'Khả năng và không chắc chắn', 'Diễn đạt về khả năng và sự không chắc chắn', '[{"word": "可能", "reading": "かのう", "meaning": "có thể", "example": "可能性がある"}, {"word": "無理", "reading": "むり", "meaning": "không thể", "example": "無理だ"}, {"word": "確実", "reading": "かくじつ", "meaning": "chắc chắn", "example": "確実な情報"}]', '[{"pattern": "〜かもしれない", "meaning": "có lẽ", "example": "雨が降るかもしれない"}, {"pattern": "〜はずだ", "meaning": "chắc là", "example": "来るはずだ"}]', 2),
('N3', 20, 'Lời khuyên và gợi ý', 'Cách đưa ra lời khuyên và gợi ý', '[{"word": "助言", "reading": "じょげん", "meaning": "lời khuyên", "example": "助言をする"}, {"word": "提案", "reading": "ていあん", "meaning": "đề xuất", "example": "提案する"}, {"word": "勧める", "reading": "すすめる", "meaning": "khuyên", "example": "本を勧める"}]', '[{"pattern": "〜たほうがいい", "meaning": "nên", "example": "休んだほうがいい"}, {"pattern": "〜ばいい", "meaning": "...thì tốt", "example": "どうすればいい"}]', 2),
('N3', 21, 'Quan điểm và lập trường', 'Diễn đạt quan điểm và lập trường', '[{"word": "立場", "reading": "たちば", "meaning": "lập trường", "example": "立場を考える"}, {"word": "観点", "reading": "かんてん", "meaning": "quan điểm", "example": "別の観点"}, {"word": "視点", "reading": "してん", "meaning": "góc nhìn", "example": "視点を変える"}]', '[{"pattern": "〜にとって", "meaning": "đối với", "example": "私にとって大切"}, {"pattern": "〜からすると", "meaning": "xét từ góc độ", "example": "彼からすると"}]', 3),
('N3', 22, 'Cảm giác và cảm xúc', 'Diễn đạt cảm giác và cảm xúc chi tiết', '[{"word": "感情", "reading": "かんじょう", "meaning": "cảm xúc", "example": "感情を表す"}, {"word": "気持ち", "reading": "きもち", "meaning": "tâm trạng", "example": "嬉しい気持ち"}, {"word": "感覚", "reading": "かんかく", "meaning": "cảm giác", "example": "不思議な感覚"}]', '[{"pattern": "〜気がする", "meaning": "có cảm giác", "example": "暑い気がする"}, {"pattern": "〜感じがする", "meaning": "có cảm giác", "example": "変な感じがする"}]', 2),
('N3', 23, 'Tình huống bất ngờ', 'Diễn đạt về tình huống bất ngờ', '[{"word": "突然", "reading": "とつぜん", "meaning": "đột nhiên", "example": "突然雨が降った"}, {"word": "急に", "reading": "きゅうに", "meaning": "đột ngột", "example": "急に寒くなった"}, {"word": "思いがけず", "reading": "おもいがけず", "meaning": "bất ngờ", "example": "思いがけず会った"}]', '[{"pattern": "〜とは思わなかった", "meaning": "không ngờ rằng", "example": "こんなに難しいとは思わなかった"}, {"pattern": "〜ことになった", "meaning": "trở thành, quyết định", "example": "留学することになった"}]', 2),
('N3', 24, 'Ôn tập tổng hợp N3', 'Ôn tập toàn bộ ngữ pháp và từ vựng N3', '[{"word": "復習", "reading": "ふくしゅう", "meaning": "ôn tập", "example": "復習する"}, {"word": "確認", "reading": "かくにん", "meaning": "xác nhận", "example": "確認する"}, {"word": "理解", "reading": "りかい", "meaning": "hiểu", "example": "理解する"}]', '[{"pattern": "総復習", "meaning": "ôn tập tổng hợp", "example": "N3文法の総復習"}]', 4),
('N3', 25, 'Luyện thi N3', 'Luyện đề thi thử và chiến lược làm bài N3', '[{"word": "試験", "reading": "しけん", "meaning": "kỳ thi", "example": "試験を受ける"}, {"word": "合格", "reading": "ごうかく", "meaning": "đậu", "example": "試験に合格する"}, {"word": "対策", "reading": "たいさく", "meaning": "đối sách", "example": "対策を立てる"}]', '[{"pattern": "試験対策", "meaning": "chiến lược thi", "example": "N3試験対策"}]', 4);
INSERT INTO public.lessons (level, lesson_number, title, description, vocabulary, grammar_points, estimated_hours) VALUES
('N2', 1, 'Ôn tập N3 & Giới thiệu N2', 'Ôn tập N3 và làm quen với mức độ N2', '[{"word": "概要", "reading": "がいよう", "meaning": "tổng quan", "example": "概要を説明する"}, {"word": "詳細", "reading": "しょうさい", "meaning": "chi tiết", "example": "詳細を確認する"}, {"word": "要点", "reading": "ようてん", "meaning": "điểm chính", "example": "要点をまとめる"}]', '[{"pattern": "〜というものだ", "meaning": "cái gọi là", "example": "人生というものだ"}, {"pattern": "〜というわけだ", "meaning": "nghĩa là", "example": "忙しいというわけだ"}]', 3),
('N2', 2, 'Ngữ pháp thư viết chính thức', 'Học cách viết văn bản chính thức', '[{"word": "拝啓", "reading": "はいけい", "meaning": "kính gửi", "example": "拝啓 時下ますます"}, {"word": "敬具", "reading": "けいぐ", "meaning": "kính thư", "example": "敬具で終わる"}, {"word": "存じます", "reading": "ぞんじます", "meaning": "biết (khiêm nhường)", "example": "存じております"}]', '[{"pattern": "〜において", "meaning": "tại, ở", "example": "会議において"}, {"pattern": "〜に関して", "meaning": "liên quan đến", "example": "この件に関して"}]', 3),
('N2', 3, 'Diễn đạt nguyên nhân kết quả', 'Cách nói về nguyên nhân và kết quả phức tạp', '[{"word": "原因", "reading": "げんいん", "meaning": "nguyên nhân", "example": "事故の原因"}, {"word": "結果", "reading": "けっか", "meaning": "kết quả", "example": "努力の結果"}, {"word": "影響", "reading": "えいきょう", "meaning": "ảnh hưởng", "example": "悪い影響"}]', '[{"pattern": "〜ことから", "meaning": "từ việc", "example": "経験したことから"}, {"pattern": "〜ゆえに", "meaning": "vì", "example": "若いゆえに"}]', 3),
('N2', 4, 'Thể hiện sự tương phản', 'Diễn đạt sự tương phản và đối lập', '[{"word": "対照", "reading": "たいしょう", "meaning": "đối chiếu", "example": "対照的だ"}, {"word": "反対", "reading": "はんたい", "meaning": "ngược lại", "example": "反対の意見"}, {"word": "逆", "reading": "ぎゃく", "meaning": "đảo ngược", "example": "逆の効果"}]', '[{"pattern": "〜に反して", "meaning": "trái với", "example": "期待に反して"}, {"pattern": "〜にもかかわらず", "meaning": "mặc dù", "example": "雨にもかかわらず"}]', 2),
('N2', 5, 'Mục đích và phương tiện', 'Diễn đạt mục đích và phương tiện', '[{"word": "手段", "reading": "しゅだん", "meaning": "phương tiện", "example": "効果的な手段"}, {"word": "方法", "reading": "ほうほう", "meaning": "phương pháp", "example": "良い方法"}, {"word": "目標", "reading": "もくひょう", "meaning": "mục tiêu", "example": "目標を達成する"}]', '[{"pattern": "〜をもって", "meaning": "bằng, với", "example": "これをもって終了"}, {"pattern": "〜を通じて", "meaning": "thông qua", "example": "経験を通じて"}]', 2),
('N2', 6, 'Giới hạn và phạm vi', 'Diễn đạt về giới hạn và phạm vi', '[{"word": "範囲", "reading": "はんい", "meaning": "phạm vi", "example": "範囲を決める"}, {"word": "限界", "reading": "げんかい", "meaning": "giới hạn", "example": "限界に達する"}, {"word": "程度", "reading": "ていど", "meaning": "mức độ", "example": "ある程度"}]', '[{"pattern": "〜に限り", "meaning": "chỉ giới hạn", "example": "今日に限り"}, {"pattern": "〜に限らず", "meaning": "không giới hạn", "example": "日本に限らず"}]', 2),
('N2', 7, 'Điều kiện tiên quyết', 'Diễn đạt điều kiện cần thiết', '[{"word": "前提", "reading": "ぜんてい", "meaning": "tiền đề", "example": "前提条件"}, {"word": "必須", "reading": "ひっす", "meaning": "bắt buộc", "example": "必須条件"}, {"word": "不可欠", "reading": "ふかけつ", "meaning": "không thể thiếu", "example": "不可欠な要素"}]', '[{"pattern": "〜上で", "meaning": "sau khi", "example": "考えた上で"}, {"pattern": "〜ないことには", "meaning": "nếu không...thì", "example": "試さないことには分からない"}]', 3),
